mapper.select:
SELECT ch.id, ch.name, ch.is_fixed isFixed, ch.is_top isTop, ch.is_publish isPublish, ch.publish_time publishTime, bu.`name` createUser, ch.create_time createTime, ch.channel_status channelStatus, bu1.`name` updateUser FROM kf_channel ch LEFT JOIN base_user bu ON ch.create_uid = bu.id LEFT JOIN base_user bu1 ON ch.edit_uid = bu1.id
AND ch.is_publish=#{entity.isPublish}
AND ch.channel_status=#{entity.channelStatus}
AND ch.category=#{entity.category}
ORDER BY ch.is_fixed DESC ,ch.is_top DESC ,ch.order_number ASC,ch.sort_time DESC

SELECT ch.id, c.id contentId, c.title, c.content_type contentType, bu.`name` updateUser FROM kf_channel ch LEFT JOIN kf_channel_content cc ON ch.id=cc.channel_id LEFT JOIN kf_content c ON cc.content_id=c.id LEFT JOIN base_user bu ON bu.id=c.update_uid
ch.id=#{id}
ORDER BY cc.order_number limit 5

mapper.update:
update kf_channel
name=#{name}, edit_uid=#{editUid}, sort_time=#{sortTime}, comment_threshold=#{commentThreshold}, view_init_num=#{viewInitNum}, view_threshold=#{viewThreshold}, hot_threshold=#{hotThreshold}, top_num=#{topNum},
where id=#{id}

mapper.select:
