#{id}

SELECT kc.id, kc.`name`, kau.nickname createUser, kau2.nickname updateUser, kc.create_time createTime, kc.is_publish isPublish, kc.is_top isTop FROM kf_channel kc LEFT JOIN kf_admin_user kau ON kc.create_uid = kau.id LEFT JOIN kf_admin_user kau2 ON kc.edit_uid = kau2.id
AND kc.category=#{category}
AND kc.channel_status=#{channelStatus}
ORDER BY kc.order_number DESC,kc.is_fixed DESC,kc.is_top Desc,kc.sort_time DESC

