mapper.select:
SELECT ct.id, ct.content, ct.create_time createTime, (SELECT INET_NTOA(ct.create_ip)) ip, c.title, ct.create_uid createUid, ct.create_devid createDevid, ct.create_user createUser FROM kf_comment ct LEFT JOIN kf_content c ON c.id=ct.cid
AND ct.stat = #{entity.stat}
AND ct.ops = #{entity.ops}
AND ct.sens = #{entity.sens}
AND ct.content LIKE CONCAT('%',#{entity.content},'%')
AND ct.cid = #{entity.cid}
AND ct.create_ip = (SELECT INET_ATON(#{entity.createIp}))
AND ct.create_time >= #{entity.beginTime}
AND ct.create_time <= #{entity.endTime}
ORDER BY ct.id DESC,ct.create_time DESC

SELECT id, cid, `type`, pid, p_uid pUid, `content`, loves, create_time createTime, create_uid createUid, create_devid createDevid, create_user createUser, sens, ops, `READ`, `stat`, comment_id commentId FROM kf_comment
sens = 0 and stat = 1
and create_uid = #{uid}
and ops = #{ops}
AND id IN
#{id}
order by id desc
limit #{offset},#{pcount}

SELECT id, cid, `type`, pid, p_uid pUid, `content`, loves, create_time createTime, create_uid createUid, create_user createUser, create_devid createDevid, sens, ops, `READ`, `stat`, comment_id commentId FROM kf_comment
sens = 0 and stat = 1
and create_uid = #{uid}
and ops = #{ops}
AND c.id IN
#{id}
order by id desc
limit #{offset},#{pcount}

SELECT ct.id, ct.cid, ct.`type`, ct.pid, ct.p_uid pUid, ct.`content`, ct.loves, ct.create_time createTime, ct.create_uid createUid, ct.create_user createUser, ct.create_devid createDevid, ct.sens, ct.ops, ct.`READ`, ct.`stat`, ct.comment_id commentId FROM kf_comment ct left JOIN kf_user u ON ct.create_uid = u.id
( (ct.create_uid != #{uid} and ct.ops = #{ops}) or (ct.create_uid = #{uid} )) and stat = 1 and u.is_delete = 1
and ct.cid = #{cid}
and ct.type = #{type}
order by ct.create_time desc
limit #{p},#{pcount}

mapper.update:
UPDATE kf_comment c
set c.`read` = 1

    c.stat = 1 and c.sens = 0 and c.`read` = 0

        and c.ops = #{ops}


        and c.create_uid = #{createUid}

UPDATE kf_comment
    SET stat = #{stat}
    WHERE
        id = #{id}
