SELECT kcc.id, kcc.title, kcc.content_type contentType, xau.nickname, kcc.update_time updateTime FROM kf_content_broadcast_bind kcbb LEFT JOIN kf_content kc ON kcbb.content_id = kc.id LEFT JOIN kf_content kcc ON kcbb.bind_id = kcc.id LEFT JOIN kf_admin_user xau ON kc.update_uid = xau.id
AND kcbb.content_id=#{contentBroadcastBind.contentId}
ORDER BY kcbb.order_number DESC, kcc.update_time DESC

SELECT
        kcb.id,
        kcb.order_number orderNumber
FROM
        kf_content_broadcast_bind kcb


        kcb.bind_id IN

            #{bindId}



       AND kcb.content_id=#{contentId}


ORDER BY field

    #{bindId}
