mapper.select:
SELECT DISTINCT c.id, c.content_type contentType, c.title, c.view_count viewCount, c.category, au.realname, cc.publish_time publishTime FROM kf_content c LEFT JOIN kf_channel_content cc ON c.id = cc.content_id LEFT JOIN kf_channel kc ON kc.id = cc.channel_id LEFT JOIN kf_admin_user au ON c.create_uid = au.id WHERE 1 = 1
AND c.category = #{categoryId}
AND cc.channel_id = #{channelId}
AND cc.publish_time >= #{startDate}
AND cc.publish_time <= #{endDate}
GROUP BY c.id ORDER BY c.view_count DESC LIMIT 0,100

SELECT count(0) c FROM kf_content AS a JOIN kf_channel_content AS b ON a.`id` = b.content_id WHERE b.is_publish = 1 AND b.is_delete = 1 AND a.check_status = 1 AND a.content_state = 1
AND a.category =#{categoryId}
AND b.channel_id = #{channelId}
