mapper.update:
UPDATE kf_channel_content
SET

     is_publish=#{channelContent.isPublish}


     ,publish_time=#{channelContent.publishTime}


     top=#{channelContent.top}

WHERE channel_id=#{channelContent.channelId} AND  content_id=#{channelContent.contentId}

mapper.select:
SELECT c.id, c.category FROM kf_channel_content cc LEFT JOIN kf_channel c ON c.id=cc.channel_id
c.channel_status=1 AND c.is_publish=1
AND cc.from_type=1 AND cc.content_id=#{contentId}

SELECT a.channel_id, a.card_type, a.top, a.publish_time, a.recommend_weight, a.order_number, a.from_type, c.id, c.category, c.content_type type, c.title, c.`introduction` `desc` , c.image, c.view_count views, c.tag, c.create_uid, c.comment_count comments, c.comment_checked_count, c.content_style, c.source_type, c.custom_url, cb.live_url, cb.review_url, cb.broadcast_status, cv.duration , cv.url video_source, ccc.card_type cardType, ccc.image cardImage FROM kf_channel_content a INNER JOIN kf_content c ON a.content_id = c.id LEFT JOIN kf_content_broadcast cb ON c.id = cb.cid LEFT JOIN kf_content_video cv on c.id=cv.cid and cv.is_delete=1 LEFT JOIN kf_channel_content_card ccc on ccc.content_id = c.id
and ccc.channel_id=#{params.chan_id}
WHERE 1 = 1
AND a.channel_id=#{params.chan_id}
AND a.is_delete = 1 AND a.check_status < 2 AND a.is_publish = 1 order by a.publish_time desc

SELECT a.content_id cid, a.card_type, a.top, a.publish_time pub_time, a.publish_time publish_time, a.recommend_weight, a.from_type, c.id, c.category, c.content_type type, c.title, c.image, c.tag, c.content_style, c.source_type, c.view_count views, c.custom_url, c.category cate, l.id live_id, l.title live_title, l.live_status AS live_stat, l.cover_img AS live_image, l.live_type_id, l.live_duration duration, l.start_time AS preview_time, l.flash_obj, l.view_count AS video_num, t. NAME AS live_class, l.view_count live_views, l.live_url, l.preview_url, l.review_url, ccc.card_type cardType, ccc.image cardImage FROM kf_channel_content a LEFT JOIN kf_content c ON a.content_id = c.id LEFT JOIN kf_live l ON a.content_id = l.id AND l.is_publish =1 AND l.is_delete =0 LEFT JOIN kf_live_type t ON l.live_type_id = t.id LEFT JOIN kf_channel_content_card ccc on ccc.content_id = c.id
and ccc.channel_id=#{params.chan_id}
WHERE 1 = 1
AND a.sort_time < #{params.signtime}
AND a.channel_id = #{params.chan_id}
AND a.is_delete = 1 AND a.check_status < 2 AND a.is_publish = 1
order by a.top desc,a.recommend_weight desc,a.order_number desc,a.publish_time desc,a.id desc
order by a.publish_time desc,a.id desc
order by a.top desc,a.order_number desc ,a.publish_time desc

SELECT id FROM kf_channel_content a WHERE 1=1
AND a.channel_id = #{params.chan_id}
and id > #{params.maxId}
AND a.is_delete = 1 AND a.is_publish = 1

SELECT cc.id, cc.content_id contentId, cc.order_number orderNumber FROM kf_channel_content cc
AND cc.content_id IN
#{contentId}
AND cc.channel_id = #{channelId}
ORDER BY field
#{contentId}

SELECT c.id, c.name FROM kf_channel_content cc LEFT JOIN kf_channel c ON c.id=cc.channel_id
c.channel_status=1 AND c.is_publish=1
AND cc.from_type=1 AND cc.content_id=#{contentId}
