mapper.select:
SELECT cu.`name`, cu.id uid, cd.`name` deptName, cj.`name` jobName, IFNULL(result.w,0) w, IFNULL(result.wp,0) wp, IFNULL(result.wpv,0) wpv, IFNULL(result.t,0) t, IFNULL(result.video,0) video, IFNULL(result.other,0) other, result.`type_id` typeId FROM kf_corp_user cu LEFT JOIN ( SELECT kc.id, kc.uid, kc.type, kc.weight, kc.type_id, kc.article_type, kc.work_type, kc.is_special, round( SUM( CASE WHEN kc.is_special = 0 && kc.article_type = 1 THEN kc.weight ELSE 0 END ), 1 ) AS w, round( SUM( CASE WHEN kc.is_special = 0 && ( kc.article_type = 2 || kc.article_type = 3 ) THEN kc.weight ELSE 0 END ), 1 ) AS wp, round( SUM( CASE WHEN kc.is_special = 0 && kc.article_type = 4 THEN kc.weight ELSE 0 END ), 1 ) AS wpv, round( SUM( CASE WHEN kc.is_special = 0 && kc.article_type = 5 THEN kc.weight ELSE 0 END ), 1 ) AS t, round( SUM( CASE WHEN kc.is_special = 0 && kc.article_type = 6 THEN kc.weight ELSE 0 END ), 1 ) AS video, SUM( CASE WHEN kc.is_special = 1 THEN 1 ELSE 0 END ) AS `other` FROM kf_kpi_count kc
WHERE kc.type = 2 AND UNIX_TIMESTAMP(first_check_time) BETWEEN UNIX_TIMESTAMP(#{startDate}) AND UNIX_TIMESTAMP(#{endDate}) GROUP BY uid ) result ON cu.id = result.uid
LEFT JOIN kf_corp_dept cd ON cu.level2_id = cd.id LEFT JOIN kf_corp_job cj ON cu.job_id = cj.id WHERE 1=1
and cu.level2_id = #{departmentId}
and cu.id = #{uid}

UPDATE kf_kpi_count
SET pv_finish_time =  #{finishTime}
WHERE
    id = #{id}
