mapper.select:
SELECT LiveBroadcast.`id`, LiveBroadcast.`title`, LiveBroadcast.start_time, LiveBroadcast.live_url liveUrl, LiveBroadcast.live_status liveStatus, LiveBroadcast.live_duration liveDuration, LiveBroadcast.preview_url previewUrl, LiveBroadcast.preview_img previewImg, LiveBroadcast.review_url reviewUrl, LiveBroadcast.cover_img coverImg, LiveBroadcast.view_count viewCount, LiveBroadcast.is_show_view_user, VideoSource.`name`, VideoSource.`image` FROM kf_live LiveBroadcast LEFT JOIN kf_video_source VideoSource ON VideoSource.id = LiveBroadcast.source_id
LiveBroadcast.is_publish = 1 AND LiveBroadcast.is_delete = 0 AND LiveBroadcast.live_type_id != 3
AND LiveBroadcast.create_time < #{signtime}
ORDER BY LiveBroadcast.create_time DESC limit #{offset},#{pcount}

SELECT lv.id, lv.title, lv.subtitle, lv.court_name courtName, lv.start_time startTime, lv.cover_img coverImg, lv.source_url sourceUrl, lv.flash_obj flashObj, lv.live_status liveStatus, lv.live_duration liveDuration, lv.live_type_id liveTypeId FROM kf_live lv
lv.is_delete = 0 AND lv.is_publish = 1
AND lv.start_time > NOW()
AND lv.live_status = #{liveStatus}
AND lv.live_type_id = #{liveTypeId}
ORDER BY lv.start_time asc limit #{offset},#{limit}

SELECT
    lv.id,
    lv.title,
    lv.subtitle,
    lv.live_status liveStatus,
    lv.cover_img coverImg,
    lv.flash_obj flashObj,
    lv.source_url sourceUrl,
    lv.court_name courtName,
    lv.live_type_id liveTypeId,
    lv.live_duration liveDuration
FROM
    kf_live lv

LEFT JOIN kf_live_court lc ON lv.court_id = lc.id


    lv.is_delete = 0 and lv.is_publish = 1

    AND  lv.creat_time  < #{signtime}


    AND  lv.live_status = #{stat}


    AND  lv.live_status = #{stat} and lv.start_time > now()


    AND  lv.live_type_id = #{type}


        AND  lv.live_type_id = #{liveTypeId}


    AND  lv.live_type_id = #{liveTypeId}

            AND  lv.court_level = #{courtLevel}


            AND  lv.case_type = #{caseType}


            AND  lv.court_id = #{court}


            AND  lc.province_id = #{provinceId}



ORDER BY

    lv.live_status asc,

    lv.is_top DESC,
    lv.live_status ASC

SELECT lv.id, lv.title, lv.subtitle, lv.court_name courtName, lv.start_time startTime, lv.cover_img coverImg, lv.source_url sourceUrl, lv.flash_obj flashObj, lv.live_status liveStatus, lv.live_duration liveDuration, lv.live_type_id liveTypeId FROM kf_live lv
lv.is_delete = 0 AND lv.is_publish = 1
AND lv.start_time > NOW() and lv.start_time < (CONCAT(CURDATE(),'23:59:59'))
AND lv.live_status = #{liveStatus}
AND lv.live_type_id = #{liveTypeId}
ORDER BY preview_show desc,id desc limit #{limit}

SELECT count(lv.id) AS count FROM kf_live lv WHERE lv.is_delete = 0 AND lv.is_publish = 1
AND lv.live_status = #{liveStatus}
AND lv.live_type_id = #{liveTypeId}

SELECT lv.id, lv.live_type_id liveTypeId, lv.title, lv.subtitle, lv.live_status liveStatus, lv.live_url liveUrl, lv.preview_url previewUrl, lv.review_url reviewUrl, lv.live_duration liveDuration, lv.view_count viewCount, lv.cover_img coverImg, lv.chatroom_notice chatroomNotice, lv.flash_obj flashObj, lv.live_content liveContent, lv.case_info caseInfo, lv.court_info courtInfo, lv.source_url sourceUrl, lv.is_publish isPublish, lv.comment_type commentType, lv.comment_status commentStatus, lv.chatroom_status chatroomStatus, lv.chatroom_keeplive chatroomKeeplive, lv.comment_count comment_count, lv.comment_checked_count commentCheckedCount, lv.source_id sourceId, lv.appvideo_view appvideoView, lc.`NAME`, lc.avatar, lc.live_count liveCount FROM kf_live lv LEFT JOIN kf_live_court lc ON lv.court_id = lc.id WHERE 1 = 1 AND CASE WHEN lv.court_id <> 0 THEN lc.stat = 1 AND lv.is_delete = 0 AND lv.is_publish = 1
AND lv.id = #{id}
ELSE lv.is_delete = 0 AND lv.is_publish = 1
AND lv.id = #{id}
END

SELECT
    work_type
FROM
    kf_kpi_count
WHERE
    type = #{type}
AND type_id = #{typeId}
AND uid = #{uid}

mapper.update:
update kf_live
id=#{id}, title=#{title}, subtitle=#{subtitle}, live_content=#{liveContent}, live_status=#{liveStatus}, start_time=#{startTime}, live_type_id=#{liveTypeId}, preview_url=#{previewUrl}, cover_img=#{coverImg}, comment_type=#{commentType}, chatroom_status=#{chatroomStatus}, source_id=#{sourceId}, comment_status=#{commentStatus}, live_type=#{liveType}, from_remark=#{fromRemark}, is_publish=#{isPublish}, is_delete=#{isDelete}, is_lock=#{isLock}, chatroom_notice=#{chatroomNotice}, is_top=#{isTop}, update_uid=#{updateUid}, update_time=#{updateTime}, live_recommend_weight=#{liveRecommendWeight},
where id=#{id}

mapper.select:
SELECT kl.id, kl.title, kl.live_status liveStatus, bu.`name` createUser, kvt.name videoType, kl.create_time createTime, kl.update_time updateTime, kl.chatroom_allcount chatroomAllcount, kl.view_count viewCount, kl.id clickUrl, kl.is_publish isPublish FROM kf_live kl LEFT JOIN base_user bu ON kl.create_uid = bu.id LEFT JOIN kf_live_type kvt ON kl.live_type_id = kvt.id
AND kl.live_type_id=#{liveTypeId}
AND kl.title LIKE CONCAT('%',#{title},'%')
AND kl.check_state=#{checkState}
AND kl.is_delete=#{isDelete}
AND kl.is_lock=#{isLock}
ORDER BY kl.live_recommend_weight DESC,kl.update_time DESC,kl.id DESC
