mapper.select:
SELECT
    count(*) AS count
FROM
    kf_content c
WHERE 1=1 AND c.is_check = 1

   	AND c.category=#{type}


     AND c.create_time BETWEEN #{startDate}
     AND #{endDate}

SELECT
   count(*) AS count
FROM
   kf_content c
WHERE 1=1
   AND c.is_check = 1

  	AND c.category=#{type}


    AND c.source_type = #{sourceType}


    AND c.create_time BETWEEN #{startDate}
    AND #{endDate}

SELECT
   count(*) AS count
FROM
   kf_content c
WHERE 1=1
   AND c.is_check = 1

  	AND c.category=#{type}


    AND c.is_legal = #{isLegal}


    AND c.create_time BETWEEN #{startDate}
    AND #{endDate}
