mapper.select:
SELECT collection.*, ( collection.columnLive + collection.ordinaryLive ) allCount FROM ( SELECT allResult.uid id, allResult. NAME, allResult.deptName, allResult.jobName, Max( CASE allResult.workTypeName WHEN '栏目直播' THEN allResult.count ELSE 0 END ) columnLive, Max( CASE allResult.workTypeName WHEN '普通直播' THEN allResult.count ELSE 0 END ) ordinaryLive FROM ( SELECT cu. NAME, cd.`name` deptName, cj.`name` jobName, cu.id uid, result.workTypeName, result.count FROM kf_corp_user cu LEFT JOIN ( SELECT k.uid, ktc. NAME workTypeName, count(0) count FROM kf_kpi_count AS k LEFT JOIN kf_kpi_type_config AS c ON c.id = k.work_type LEFT JOIN kf_kpi_type_config AS ktc ON c.pid = ktc.id WHERE 1 = 1 AND k.type = 9 AND k.first_check_time IS NOT NULL AND k.first_check_time >= #{startDate} AND k.first_check_time < #{endDate} AND k.is_special = 0 AND ktc.pid = 0 GROUP BY k.uid, ktc.id ) result ON cu.id = result.uid LEFT JOIN kf_corp_dept cd ON cd.id = cu.level2_id LEFT JOIN kf_corp_job cj ON cj.id = cu.job_id where 1=1
AND cu.id = #{uid}
AND cd.id = #{departmentId}
) allResult GROUP BY allResult.uid ) collection

SELECT d.id FROM kf_kpi_count AS k INNER JOIN kf_demand AS d ON d.id = k.type_id WHERE 1 = 1
AND k.uid = #{uid}
AND k.type = 9 AND k.first_check_time IS NOT NULL AND k.first_check_time >= #{startDate} AND k.first_check_time < #{endDate} GROUP BY d.id

select result.* from ( SELECT k.id as id , l.id AS liveId, l.title,
IF (k.is_special = 1, "是", "否") isSpecial, k. NAME, k.first_check_time firstCheckTime, CASE l.live_type WHEN 0 THEN '无' WHEN 1 THEN '栏目' WHEN 2 THEN '其他' ELSE '无' END liveTypeName, CONCAT(tc. NAME, "(", ktc. NAME, ")") AS workName, k.pv_finish_time, tc.pv_limit pvLimit, k.remarks, l.review_url AS url, l.from_remark fromRemark FROM kf_kpi_count AS k LEFT JOIN kf_live AS l ON l.id = k.type_id LEFT JOIN kf_kpi_type_config AS tc ON k.work_type = tc.id LEFT JOIN kf_kpi_type_config AS ktc ON tc.pid = ktc.id WHERE 1 = 1
AND k.uid = #{uid}
AND k.type = 9 AND k.first_check_time IS NOT NULL AND k.first_check_time >= #{startDate} AND k.first_check_time < #{endDate} ) result where 1=1
AND result.liveTypeName = #{liveTypeName}
AND ( result.liveId = #{title} OR result.title = #{title} )
