mapper.select:
SELECT cu.id, cu. NAME, cd.`name` deptName, cj.`name` jobName, IFNULL( countResult.originalVideo, 0 ) originalVideo, IFNULL( countResult.originalLater, 0 ) originalLater, IFNULL( countResult.animationDesing, 0 ) animationDesing, IFNULL(countResult.reviewVideo, 0) reviewVideo, IFNULL(countResult.reprintVideo, 0) reprintVideo, IFNULL(countResult.dubbing, 0) dubbing, IFNULL( countResult.animationScript, 0 ) animationScript, IFNULL(countResult.other, 0) other FROM kf_corp_user cu LEFT JOIN ( SELECT result.uid, result. NAME, sum( result.原创视频（含VR） ) 'originalVideo', sum( result.原创栏目视频动画后期 ) 'originalLater', sum(result.动画原画设计) 'animationDesing', sum(result.评论视频) 'reviewVideo', sum(result.转载视频) 'reprintVideo', sum(result.配音) 'dubbing', sum( result.动画、策划脚本 ) 'animationScript', sum(result.其他) 'other' FROM ( SELECT k.uid, k.`name`, #count(0) count, k.work_type, ( CASE k.work_type WHEN '1' THEN count(0) ELSE 0 END ) '原创视频（含VR）', ( CASE k.work_type WHEN '2' THEN count(0) ELSE 0 END ) '原创栏目视频动画后期', ( CASE k.work_type WHEN '3' THEN count(0) ELSE 0 END ) '动画原画设计', ( CASE k.work_type WHEN '4' THEN count(0) ELSE 0 END ) '评论视频', ( CASE k.work_type WHEN '5' THEN count(0) ELSE 0 END ) '转载视频', ( CASE k.work_type WHEN '6' THEN count(0) ELSE 0 END ) '配音', ( CASE k.work_type WHEN '7' THEN count(0) ELSE 0 END ) '动画、策划脚本', ( CASE k.work_type WHEN '8' THEN count(0) ELSE 0 END ) '其他' FROM kf_kpi_count AS k INNER JOIN kf_demand AS d ON d.id = k.type_id LEFT JOIN kf_kpi_type_config AS c ON c.id = k.work_type WHERE 1 = 1 AND k.type = 4 AND k.first_check_time IS NOT NULL AND k.first_check_time >= #{startDate} AND k.first_check_time <#{endDate}
AND (k.pv_finish_time <=#{pvEndDate} or k.pv_finish_time IS NULL)
AND k.is_special = 0 GROUP BY k.uid, k.work_type ) result GROUP BY result.uid ) countResult ON cu.id = countResult.uid LEFT JOIN kf_corp_dept cd ON cd.id = cu.level2_id LEFT JOIN kf_corp_job cj ON cj.id = cu.job_id where 1=1
AND cu.id = #{uid}
AND cd.id = #{departmentId}

SELECT d.id FROM kf_kpi_count AS k INNER JOIN kf_demand AS d ON d.id = k.type_id WHERE 1 = 1
AND k.uid = #{uid}
AND k.type = 4 AND k.first_check_time IS NOT NULL AND k.first_check_time >= #{startDate} AND k.first_check_time < #{endDate} GROUP BY d.id

SELECT c.id AS cid, c.title,
IF ( c.content_type = 2, "文章", "视频" ) type FROM kf_content c LEFT JOIN kf_content_video cv ON c.id = cv.cid WHERE cv.did = #{uid}
UNION
SELECT c.id AS cid, c.title,
IF ( c.content_type = 2, "文章", "视频" ) type FROM kf_content c LEFT JOIN kf_content_article ca ON c.id = ca.cid WHERE ca.did = #{uid}
