mapper.select:
SELECT count(c.id) AS num, au.realname FROM kf_content c LEFT JOIN kf_admin_user au ON c.create_uid = au.id WHERE 1 = 1 AND c.first_check_time
BETWEEN #{startDate} AND #{endDate}
GROUP BY c.create_uid ORDER BY num LIMIT 10

SELECT count(id) count, STR_TO_DATE( first_check_time, '%Y-%m-%d' ) date, source_type sourceType, category FROM kf_content WHERE 1 = 1
AND first_check_time BETWEEN #{startDate} AND #{endDate}
AND source_type != 0 AND content_type IN (2, 3, 4) GROUP BY source_type, category, date

SELECT count(id) AS sum, date_format(start_time, "%Y-%m-%d") AS nowDate FROM kf_live WHERE 1 = 1
AND start_time BETWEEN #{startDate} AND #{endDate}
AND live_status = 1 GROUP BY nowDate UNION SELECT count(id) AS sum, date_format(start_time, "%Y-%m-%d") AS nowDate FROM kf_live WHERE 1 = 1
AND start_time BETWEEN #{startDate} AND #{endDate}
AND live_status = 1
