mapper.select:
SELECT c.id, c.title, c.introduction, c.image, c.cover_img coverImg, c.source_type sourceType, c.is_legal isLegal, c.create_uid createUid, (SELECT recommend_weight FROM kf_channel_content WHERE content_id=#{id} LIMIT 1) recommendWeight, au.`name` createUser
FROM kf_content c LEFT JOIN base_user au ON c.create_uid = au.id
AND c.id = #{id}

SELECT c.id, c.title, c.introduction, c.image, c.custom_url customUrl, (SELECT type FROM kf_channel_content WHERE content_id=#{id} LIMIT 1) cardType
FROM kf_content c
AND c.id = #{id}

SELECT c.id, ci.cid, c.title, c.content_type contentType, c.image, c.custom_url customUrl, c.view_count viewCount, c.create_time createTime, c.update_time updateTime, c.check_status checkStatus, c.content_state contentState, ci.desc_type descType, ci.num, ci.stat, cig.`desc`, cig.image imageGroup, cig.`order`, cv.duration FROM (select * from kf_content cc
cc.content_type BETWEEN 1 and 4
AND cc.content_state=#{entity.contentState}
AND cc.check_status=#{entity.checkStatus}
ORDER BY cc.create_time DESC limit #{entity.page},#{entity.limit}) c LEFT JOIN kf_content_image ci ON c.id = ci.cid LEFT JOIN kf_content_image_group cig ON c.id = cig.cid LEFT JOIN kf_content_video cv ON c.id = cv.cid

SELECT c.id, c.title, cc.recommend_weight recommendWeight, cc.is_publish isPublish, cc.top isTop, cc.order_number orderNumber, c.content_type contentType, c.create_time createTime, IF (c.update_time != '',c.update_time,c.create_time) updateTime, c.view_count viewCount, bu.`name` createUser, c.content_state contentState, bu1.`name` updateUser FROM kf_content c LEFT JOIN base_user bu ON c.create_uid = bu.id LEFT JOIN base_user bu1 ON c.update_uid = bu1.id LEFT JOIN kf_channel_content cc ON cc.content_id = c.id AND cc.from_type=1
AND c.content_state=#{entity.contentState}
AND c.content_type=#{entity.contentType}
AND c.category=#{entity.category}
AND c.create_time >= #{entity.beginTime}
AND c.create_time <= #{entity.endTime}
AND c.id=#{entity.id}
AND c.title LIKE CONCAT('%',#{entity.title},'%')
AND bu.name=#{entity.createUser}
AND (c.check_status=#{entity.checkStatus} OR c.is_check=1) AND cc.check_status=1
AND c.id NOT IN
#{id}
AND cc.channel_id=#{entity.channelId}
AND cc.is_publish=#{entity.isPublish}
GROUP BY c.id UNION SELECT l.id, l.title, cc.recommend_weight recommendWeight, cc.is_publish isPublish, cc.top isTop, cc.order_number orderNumber, 22 contentType, l.create_time createTime, IF(l.update_time!='',l.update_time,l.create_time) updateTime, l.view_count viewCount, bu.name createUser, l.is_delete contentState, IF(bu1.name!='',bu1.name,bu.name) updateUser FROM kf_live l LEFT JOIN base_user bu ON l.create_uid = bu.id LEFT JOIN base_user bu1 ON l.update_uid = bu1.id INNER JOIN kf_channel_content cc ON cc.content_id = l.id AND cc.from_type=2
AND l.is_delete=0
AND l.is_delete=1
AND cc.type=#{entity.contentType}
AND cc.cate=#{entity.category}
AND l.create_time >= #{entity.beginTime}
AND l.create_time <= #{entity.endTime}
AND l.id=#{entity.id}
AND l.title LIKE CONCAT('%',#{entity.title},'%')
AND bu.name=#{entity.createUser}
AND l.check_state=#{entity.checkStatus} AND cc.check_status=1
AND l.id NOT IN
#{id}
AND cc.channel_id=#{entity.channelId}
AND cc.is_publish=#{entity.isPublish}
GROUP BY l.id ORDER BY isTop DESC,recommendWeight DESC,orderNumber DESC,updateTime DESC

SELECT c.id, c.title, c.content_type contentType, cu.url video, cu.duration FROM kf_content c LEFT JOIN kf_content_video cu ON c.id = cu.cid
AND c.content_state=#{entity.contentState}
AND c.title LIKE CONCAT('%',#{entity.title},'%')
AND c.check_status=#{entity.checkStatus}
AND cu.url!=''
ORDER BY c.create_time DESC

SELECT
        c.id,
        c.title
FROM
        kf_content_broadcast_bind cbb
        LEFT JOIN kf_content c ON cbb.content_id = c.id


        AND cbb.content_id = #{entity.id}


        AND c.content_state = #{entity.contentState}


        AND c.check_status = #{entity.checkStatus}

SELECT DISTINCT c.id, c.title, cc.top isTop, cc.is_publish isPublish, kd.contentType contentTypeCh, c.create_time createTime, c.update_time updateTime, c.view_count viewCount, bu.`name` createUser, bu1.`name` updateUser, c.id clickUrl, kvcb.is_recommend isRecommend FROM kf_content c LEFT JOIN base_user bu ON c.create_uid = bu.id LEFT JOIN base_user bu1 ON c.update_uid = bu1.id LEFT JOIN kf_channel_content cc ON cc.content_id = c.id LEFT JOIN kf_content_video kvcb ON kvcb.cid = c.id LEFT JOIN kf_channel ccc ON cc.channel_id = ccc.id LEFT JOIN kf_dictionary kd ON c.content_type = kd.id
AND c.content_state=#{entity.contentState}
AND c.content_type=#{entity.contentType}
AND c.content_type IN (4,9)
AND c.title LIKE CONCAT('%',#{entity.title},'%')
AND c.check_status=#{entity.checkStatus}
AND cc.is_publish=#{entity.isPublish}
AND cc.channel_id=#{entity.channelId}
AND c.is_check=#{entity.isCheck}
AND ccc.category=#{entity.category}
ORDER BY id DESC,updateTime DESC

mapper.update:
update kf_content
id=#{id}, content_type=#{contentType}, title=#{title}, introduction=#{introduction}, image=#{image}, content_state=#{contentState}, source_type=#{sourceType}, is_legal=#{isLegal}, category=#{category}, video_type=#{videoType}, update_uid=#{updateUid}, update_time=#{updateTime}, is_relation=#{isRelation}, is_check=#{isCheck}, is_duty=#{isDuty},
where id=#{id}

mapper.select:
SELECT c.id, c.title, c.content_type contentType, au.`name` createUser, if(c.update_time!='',c.update_time,c.create_time) updateTime, cc.from_type fromType FROM kf_content c LEFT JOIN base_user au ON if(c.update_uid!=0,c.update_uid,c.create_uid)=au.id INNER JOIN kf_channel_content cc on c.id=cc.content_id
AND cc.channel_id=#{entity.channelId}
AND cc.from_type=1 AND cc.is_delete=1
AND c.content_state=#{entity.contentState}
AND c.title LIKE CONCAT('%',#{entity.title},'%')
AND au.`name`=#{entity.updateUser}
AND c.check_status=#{entity.checkStatus}
AND (c.is_check=1 OR c.check_status=#{entity.checkStatus})
UNION SELECT l.id, l.title, 22 contentType, au.`name` createUser, if(l.update_time!='',l.update_time,l.create_time) updateTime, cc.from_type fromType FROM kf_live l LEFT JOIN base_user au ON if(l.update_uid!=0,l.update_uid,l.create_uid)=au.id INNER JOIN kf_channel_content cc ON l.id=cc.content_id
AND cc.channel_id=#{entity.channelId}
AND cc.from_type=2 AND cc.is_delete=1
AND l.is_delete=1
AND l.is_delete=0
AND l.title LIKE CONCAT('%',#{entity.title},'%')
AND au.`name`=#{entity.updateUser}
AND l.check_state=#{entity.checkStatus}
ORDER BY id DESC,updateTime DESC

SELECT c.id, cc.channel_id channelId FROM kf_content c LEFT JOIN kf_channel_content cc ON cc.content_id = c.id
c.check_status=#{checkStatus}
AND cc.channel_id IN
#{id}

mapper.update:
update kf_content
id=#{id}, title=#{title}, introduction=#{introduction}, image=#{image}, is_legal=#{isLegal}, update_uid=#{updateUid}, update_time=#{updateTime}, is_check=#{isCheck}, check_status=#{CheckStatus},
where id=#{id}

mapper.select:
SELECT c.id, c.comment_count commentCount, c.comment_checked_count commentCheckedCount FROM kf_content c
AND c.id IN
#{id}

SELECT kc.id, kc.title, kc.check_status checkStatus, kau.`name` updateUser, kc.update_time updateTime, kc.content_type contentType FROM kf_content kc LEFT JOIN base_user kau ON kc.update_uid = kau.id LEFT JOIN kf_channel_content kcc ON kc.id = kcc.content_id
AND kc.title LIKE CONCAT('%',#{title},'%')
AND kcc.channel_id=#{channelId}
AND kc.is_check=#{isCheck}
AND kc.create_uid=#{createUid}
AND kc.content_state=#{contentState}
AND kc.check_status=#{checkStatus}
ORDER BY kc.id DESC
