mapper.select:
SELECT sc.id, sc.title, group_concat( cs.target_cid ORDER BY cs.order_number ASC ) AS target_cid FROM kf_special_catalog sc LEFT JOIN kf_content_special cs ON cs.catalog_id = sc.id WHERE sc.is_delete=1 and cs.is_delete=1 and cs.is_publish=1
and sc.cid = #{cid} and cs.content_id = #{cid}
GROUP BY sc.id ORDER BY sc.order_number ASC

SELECT c.*, cc.publish_time, cc.channel_id, cs.order_number FROM kf_content c LEFT JOIN kf_channel_content cc ON c.id = cc.content_id LEFT JOIN kf_content_special cs ON cs.target_cid = c.id WHERE 1=1
and cs.content_id = #{entity.cid}
and cs.catalog_id = #{entity.catalogId}
AND c.id IN
#{id}
GROUP BY c.id ORDER BY cs.order_number ASC
